import './App.css';
import { useEffect } from 'react';
import { animate } from 'animejs';

function App() {
  useEffect(() => {
    // Initial animation: letters appearing from left to right
    animate('.zeronia-letter', {
      opacity: [0, 1],
      translateX: [-50, 0],
      easing: 'out(cubic)',
      duration: 800,
      delay: (el, i) => i * 100
    });

    // Slight scale bounce effect as each letter appears
    animate('.zeronia-letter', {
      scale: [0.8, 1],
      easing: 'out(back)',
      duration: 600,
      delay: (el, i) => i * 100 + 200
    });

    // After the initial animation completes (700ms for all letters + 800ms duration)
    // Move the entire header container (logo + text) to top left and shrink
    setTimeout(() => {
      const headerContainer = document.querySelector('.header-container');
      const rect = headerContainer.getBoundingClientRect();

      // Calculate viewport dimensions and center
      const viewportWidth = window.innerWidth;
      const viewportHeight = window.innerHeight;

      // Current center of the element (it's centered in viewport)
      const currentCenterX = rect.left + rect.width / 2;
      const currentCenterY = rect.top + rect.height / 2;

      // After scaling to 0.5, calculate the target position
      // Use responsive padding based on viewport size
      const paddingX = Math.max(100, viewportWidth * 0.06); // Even more padding on the left
      const paddingY = Math.max(30, viewportWidth * 0.02);
      const scaledWidth = rect.width * 0.5;
      const scaledHeight = rect.height * 0.5;

      // Target position (center of the scaled element should be at padding + half the scaled size)
      const targetCenterX = paddingX + scaledWidth / 2;
      const targetCenterY = paddingY + scaledHeight / 2;

      // Calculate the movement needed
      const moveX = targetCenterX - currentCenterX;
      const moveY = targetCenterY - currentCenterY;

      // Smoothly animate to top-left corner while scaling
      animate('.header-container', {
        translateX: [0, moveX],
        translateY: [0, moveY],
        scale: [1, 0.5],
        easing: 'inOut(cubic)',
        duration: 1200
      });

      // Fade in the logo
      animate('.zeronia-logo', {
        opacity: [0, 1],
        easing: 'out(cubic)',
        duration: 800,
        delay: 200
      });
    }, 1500); // Wait for initial animation to complete
  }, []);

  return (
    <div className="App">
      <div className="header-container">
        <img 
          src="/zeronialogo-2021.png" 
          alt="Zeronia Logo" 
          className="zeronia-logo"
        />
        <div className="zeronia-container">
          <div className="zeronia-text">
            <span className="zeronia-letter">Z</span>
            <span className="zeronia-letter">E</span>
            <span className="zeronia-letter">R</span>
            <span className="zeronia-letter">O</span>
            <span className="zeronia-letter">N</span>
            <span className="zeronia-letter">I</span>
            <span className="zeronia-letter">A</span>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
