import './App.css';
import { useEffect } from 'react';
import anime from 'animejs';

function App() {
  useEffect(() => {
    // Animate the text drawing effect
    anime({
      targets: '.zeronia-text path',
      strokeDashoffset: [anime.setDashoffset, 0],
      easing: 'easeInOutSine',
      duration: 2500,
      delay: function(el, i) { return i * 150 },
      direction: 'normal',
      loop: false
    });

    // Fade in the fill after drawing
    anime({
      targets: '.zeronia-text path',
      fill: ['rgba(255, 255, 255, 0)', 'rgba(255, 255, 255, 1)'],
      easing: 'easeInOutQuad',
      duration: 1500,
      delay: 2000
    });
  }, []);

  return (
    <div className="App">
      <svg className="zeronia-text" viewBox="0 0 600 120" xmlns="http://www.w3.org/2000/svg">
        <text x="50%" y="50%" dominantBaseline="middle" textAnchor="middle">
          ZERONIA
        </text>
      </svg>
    </div>
  );
}

export default App;
