import './App.css';
import { useEffect } from 'react';
import { animate } from 'animejs';

function App() {
  useEffect(() => {
    const letters = document.querySelectorAll('.zeronia-letter');

    // Animate each letter appearing from left to right with a drawing effect
    animate('.zeronia-letter', {
      opacity: [0, 1],
      translateX: [-50, 0],
      easing: 'out(cubic)',
      duration: 800,
      delay: (el, i) => i * 100
    });

    // Slight scale bounce effect as each letter appears
    animate('.zeronia-letter', {
      scale: [0.8, 1],
      easing: 'out(back)',
      duration: 600,
      delay: (el, i) => i * 100 + 200
    });
  }, []);

  return (
    <div className="App">
      <div className="zeronia-text">
        <span className="zeronia-letter">Z</span>
        <span className="zeronia-letter">E</span>
        <span className="zeronia-letter">R</span>
        <span className="zeronia-letter">O</span>
        <span className="zeronia-letter">N</span>
        <span className="zeronia-letter">I</span>
        <span className="zeronia-letter">A</span>
      </div>
    </div>
  );
}

export default App;
